import React from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import './App.css';
import Footer from './components/Footer';
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ProjectsPage from './pages/ProjectsPage';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pages: [
        {
          page: 'home',
          title: 'home',
          el: HomePage,
          path: '/'
        },
        {
          page: 'about',
          title: 'about me',
          el: AboutPage,
          path: '/about'
        },
        {
          page: 'projects',
          title: 'projects',
          el: ProjectsPage,
          path: '/projects'
        }
      ]
    }
  }
  makeItems = (pages) => {
    return pages.map(page => {
      var Comp = page.el;
      return <Route path={page.path} exact render={() => <Comp title={page.title} />} />
    })
  }
  makeLinks = (pages) => {
    return pages.map(page => {
      return <Link className="nav-link" to={page.path}>{page.page}</Link>
    }
  )}

  render() {
    return (
      <nav>
        <Router>
          <nav className="listofel">
            {this.makeLinks(this.state.pages)}
          </nav>
          {this.makeItems(this.state.pages)}
          <Footer />
        </Router>
      </nav>
    );
  }
}

export default App;
